mapper.select:
select price, target_date from (select s.price, DATE_FORMAT(o.reg_datetime, '%Y-%m-%d') as target_date, ROW_NUMBER() over (partition by o.reg_datetime ORDER BY o.reg_datetime DESC) as order_number from product left join size on product.no = size.product_no left join sell s on size.no = s.size_no left join purchase p on size.no = p.size_no left join `order` o on s.no = o.sell_no or p.no = o.purchase_no WHERE product.no = #{product_no}
AND DATE_FORMAT(o.reg_datetime, '%Y-%m-%d') BETWEEN DATE_ADD(NOW(), INTERVAL -1 MONTH) AND NOW()
AND DATE_FORMAT(o.reg_datetime, '%Y-%m-%d') BETWEEN DATE_ADD(NOW(), INTERVAL -3 MONTH) AND NOW()
AND DATE_FORMAT(o.reg_datetime, '%Y-%m-%d') BETWEEN DATE_ADD(NOW(), INTERVAL -6 MONTH) AND NOW()
AND DATE_FORMAT(o.reg_datetime, '%Y-%m-%d') BETWEEN DATE_ADD(NOW(), INTERVAL -1 YEAR) AND NOW()
AND o.no is not null GROUP BY DATE_FORMAT(o.reg_datetime, '%Y-%m-%d'), o.no ORDER BY DATE_FORMAT(o.reg_datetime, '%Y-%m-%d') DESC) as t WHERE order_number = 1;

mapper.update:
update product
SET views = views + 1
WHERE no = #{no}
