mapper.select:
select *
from sell
         left join `order` o on sell.no = o.sell_no
WHERE size_no = #{size_no}
  AND price = #{price}
  AND o.no is null
order by sell.reg_datetime
LIMIT 1;

select sell.no,sell.expiration_date,p.en_name,p.kor_name,JSON_EXTRACT(p.images,'$[0]')as images,s.size from sell
left join size s on sell.size_no = s.no
left join product p on s.product_no = p.no
where user_no =#{user_no} and

    expiration_date  >= now();



    expiration_date  < now();

select s.no,d.status,ss.type,JSON_EXTRACT(p.images,'$[0]')as images,p.en_name,p.kor_name from sell s
inner join `order` o on s.no = o.sell_no
left join sell_stock ss on o.no = ss.order_no
left join size s2 on s.size_no = s2.no
left join product p on p.no = s2.product_no
left join delivery d on o.no = d.order_no

where user_no = #{user_no}

    and d.status = #{status}


    and ss.type = #{type}

select sell.no, JSON_EXTRACT(p.images,'$[0]')as images,p.kor_name,p.en_name,s.size ,o.order_status from sell
left  join `order` o on sell.no = o.sell_no
left join size s on sell.size_no = s.no
left join product p on s.product_no = p.no
where user_no = #{user_no}
and order_status = #{order_status}
